sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
/* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
/* Optional GlideRecord */ event) {

	// Add your code here
	var insLbl = 'Install Cost';
	var annLbl = 'Annual Cost';

	if(current.category == 'hardware'){
		insLbl = 'Purchase Cost';
		annLbl = 'Subscription/Support Cost';
	}

	template.print('<table>');
	template.print('<tr><td></td><td class="field-label">Estimated:</td><td class="field-label">Actual:</td></tr>');
	template.print('<tr><td class="field-label">' + insLbl + ':</td><td class="form-control">' + current.predicted_install_cost.getCurrencyDisplayValue() + '</td><td class="form-control">' + current.cost_per_unit.getCurrencyDisplayValue() + '</td></tr>');
	template.print('<tr><td class="field-label">' + annLbl + ':</td><td class="form-control">' + current.predicted_annual_cost.getCurrencyDisplayValue() + '</td><td class="form-control">' + current.annual_cost.getCurrencyDisplayValue() + '</td></tr>');
	template.print('</table>');
	template.print('<p></p>');
	template.print('<table><tr><td class="field-label">Reason for cost difference:</td>');
	template.print('<td class="form-control text-area">' + current.reason_for_difference + '</td></tr></table>');

})(current, template, email, email_action, event);
