current.assigned_to = getApprover();

function getApprover() {
	var app = new GlideRecord('sysapproval_approver');
	app.addQuery('sysapproval', current.sys_id);
	app.addQuery('state', 'approved');
	app.orderByDesc('sys_created_on');
	app.query();
	if (app.next())
		return app.getValue('approver');
	return '';
}

current.assigned_to = getApprover();

function getApprover() {
	var app = new GlideRecord('sysapproval_approver');
	app.addQuery('sysapproval', current.sys_id);
	app.addQuery('state', 'approved');
	app.orderByDesc('sys_created_on');
	app.query();
	if (app.next())
		return app.getValue('approver');
	return '';
}

